import React, { Component } from "react";
import { TweenLite, gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
import image1 from "./image-1.jpg";
import image2 from "./image-2.jpg";
import image3 from "./image-3.jpg";
import image4 from "./image-4.jpg";

import "./ProjectList.scss";

gsap.registerPlugin(Draggable);
const containerId = "lib__wrapper";
const containerSelector = "." + containerId;
const rowClass = "row";
const rowSelector = "." + rowClass;
const imageClass = "lib__item";
const imageSelector = "." + imageClass;

const useInertia = true;
const useCenterGrid = true;

let rows, imgMidIndex, rowMidIndex;
const rowArray = [];
const imgRep = [];

let boxWidth,
  boxHeight,
  gutter,
  horizSpacing,
  vertSpacing,
  horizOffset,
  vertOffset,
  winMidX,
  winMidY,
  lastCenteredElem;

class projectList extends Component {
  constructor(props) {
    super(props);

    this.myRef = React.createRef();
  }

  componentDidMount() {
    gsap.set(containerSelector, { willChange: "transform" });

    this.createImageGrid();/*
    createMask();
    lastCenteredElem = document.querySelectorAll(imageSelector)[
      (rowMidIndex - 1) * imgNum + imgMidIndex
    ];*/

    this.createDraggable();

    /*setStyles();

    resize();
    window.addEventListener("resize", resize);*/
  };

  createImageGrid() {
      winMidX = 200 / 2;
      winMidY = 150 / 2;
      boxWidth = 200 * 0.35;
      boxHeight = 150 * 0.35;
      gutter = 200 * 0.2;
      horizSpacing = boxWidth + gutter;
      vertSpacing = boxHeight + gutter;
      horizOffset = -(imgMidIndex * horizSpacing + boxWidth / 2) + winMidX;
      vertOffset = -(rowMidIndex * vertSpacing + boxHeight / 2) + winMidY;

      // Reset our container and rows
      gsap.set(containerSelector, { x: 0, y: 0 });

      imageSelector.forEach(function (row, i) {
        gsap.set(row, {
          x() {
            return horizOffset;
          },
          y() {
            return i * vertSpacing + vertOffset;
          }
        });

        gsap.set(containerSelector.querySelectorAll(imageSelector), {
          width: boxWidth,
          height: boxHeight * 1,
          x(index) {
            return index * horizSpacing;
          }
        });
        const tl = gsap.timeline();

        tl.from(containerSelector.querySelectorAll(imageSelector), {
          scale: 0.2,
          duration: 3,
          y: 500,
          ease: "sine.inOut",
          opacity: 0
        });
      });
  }

  createDraggable() {
    const options = {
      trigger: containerSelector,
      dragResistance: 0.4,
      resistance: 400,
    };

    return Draggable.create(containerSelector, options);
  }

  mouseMove = e => {
    TweenLite.to(this.myRef.current, 0.5, {
      css: {
        left: e.pageX,
        top: e.pageY
      },
      delay: 0.03
    });
  };

  handleMouseEnter = () => {
    TweenLite.to(this.myRef.current, 0, {
      css: {
        opacity: "1"
      }
    });
  };

  handleMouseLeave = () => {
    TweenLite.to(this.myRef.current, 0, {
      css: {
        opacity: "0"
      }
    });
  };

  onHover = e => {
    if (e.target.className === "project p-1") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image1 + ")" }
      });
    }
    if (e.target.className === "project p-2") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image2 + ")" }
      });
    }
    if (e.target.className === "project p-3") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image3 + ")" }
      });
    }
    if (e.target.className === "project p-4") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image4 + ")" }
      });
    }
  };

  render() {
    const dragInfo = {
      position: 'absolute', 
      bottom: '20%',
    };
    return (
      <React.Fragment>
        <div className="Container__wrapper Container__absolute Container__center Container__masked Cursor__wrapper">
          <div className="Cursor__cursor">
            <svg xmlns="http://www.w3.org/2000/svg" width="56" height="56" viewBox="0 0 56 56">
              <circle cx="28" cy="28" r="27" fill="transparent" stroke="#000" strokeMiterlimit="10" strokeWidth="2">
              </circle>
              <g>
                <polygon points="28.064 9.543 20.871 26.834 35.129 26.834 28.064 9.543" stroke="#000" strokeLinejoin="round" strokeWidth="2">
                </polygon>
                <polygon points="27.936 46.293 35.129 26.834 20.871 26.834 27.936 46.293" fill="none" stroke="#000" strokeLinejoin="round" strokeWidth="2">
                </polygon>
                <ellipse cx="28" cy="26.834" rx="7.129" ry="6.679" fill="#fff" stroke="#000" strokeMiterlimit="10" strokeWidth="2">
                </ellipse>
              </g>
            </svg>
          </div>
        </div>
        <div className="Container__wrapper Container__center">
          <div>
            <div className="Intro__wrapper">
              <h1 className="Heading__wrapper styles__font-size-xl styles__font-family-serif-bold Intro__title">Postcards&nbsp;from isüèùlation</h1>
              <div className="Heading__wrapper styles__font-size-sm Intro__desc">An ongoing series of interactive experiences on how our lives changed since locked down at home.</div>
            </div>
          </div>
          <div className="Container__wrapper Container__absolute Container__center mask"></div>
          <div className="Container__wrapper Container__absolute Container__center">
              <div className="Heading__wrapper styles__font-size-xs" style={dragInfo}>DRAG TO EXPLORE</div>
              <div className="lib__wrapper ">
                <div className="lib__item">
                  <a data-hide-cursor="true" className="styles__font-size-xs styles__font-family-sans-serif styles__double-border Thumb__wrapper Thumb__size-medium Thumb__layout-horizontal" draggable="false" href="/postcards/my-week">
                    <img className="Thumb__cover" alt="My week by Raffaele Filiberti ‚Äî sabato.studio" draggable="false" src={image1}/>
                    <div className="Thumb__title">My week by Raffaele Filiberti ‚Äî sabato.studio
                    </div>
                  </a>
                </div>
                  <div className="lib__item">
                    <a data-hide-cursor="true" className="styles__font-size-xs styles__font-family-sans-serif styles__double-border Thumb__wrapper Thumb__size-medium Thumb__layout-horizontal" draggable="false" href="/postcards/my-week">
                      <img className="Thumb__cover" alt="My week by Raffaele Filiberti ‚Äî sabato.studio" draggable="false" src={image1}/>
                      <div className="Thumb__title">My week by Raffaele Filiberti ‚Äî sabato.studio
                      </div>
                    </a>
