import React from "react";

import ProjectList from "./components/ProjectList/ProjectList";
import "./App.css";

function App() {
  window.addEventListener("DOMContentLoaded", () => {
	  gsap.defaults({
	    ease: "Power3.inOut"
	  });

	  window.onmousemove = (e) => {
	    gsap.to("#cursor", {
	      duration: 0.5,
	      left: e.clientX,
	      top: e.clientY,
	      visibility: "visible"
	    });
	  };

	  gsap.utils.toArray("#text").forEach((text) => {
	    text.addEventListener("mouseenter", () => {
	      gsap.to("#cursor", {
	        duration: 0.2,
	        backgroundColor: "#ffffff",
	        height: 100,
	        width: 100
	      });

	      gsap.to(text, {
	        duration: 0.3,
	        color: "#ffffff"
	      });
	    });

	    text.addEventListener("mouseleave", () => {
	      gsap.to("#cursor", {
	        duration: 0.2,
	        backgroundColor: "transparent",
	        height: 70,
	        width: 70
	      });

	      gsap.to(text, {
	        duration: 0.3,
	        color: "transparent"
	      });
	    });
	  });
	});

  return (
    <ProjectList className="App"/>
  );
}

export default App;
