      },
      position: "absolute",
      backgroundSize: "cover",
      backgroundPosition: "center",
      top: 0,
      left: 0
    });

    gsap.set(rowSelector, {
      position: "absolute"
    });
  }

  createImageGrid() {
      let tlRepeat = gsap;
      horizOffset = 0;
      vertOffset = 0;
      var myArray = document.querySelectorAll(imageSelector);
      for(let i = 0; i < myArray.length; i++){
        tlRepeat.set(myArray[i], {y: vertOffset * 1.5, x: horizOffset * 5});
        horizOffset += 100;
        console.log(horizOffset)
      }
      /*document.querySelectorAll(imageSelector).forEach(function(entry) {

        tlRepeat.set(imageSelector, {y: horizOffset, x: 100 / 2});
        horizOffset += 100;
      });*/
  }

  createDraggable() {
    let options = {
      trigger: ".mask",
      dragResistance: 0.4,
      resistance: 400,
      onDrag: updateCenterElem,
    }

    if(useInertia) {
      options.inertia = true;
      options.onThrowUpdate = updateCenterElem;

      if(useCenterGrid) {
        options.onThrowComplete = centerGrid;
      }
    } else if(useCenterGrid) { // No inertia
      options.onDragEnd = centerGrid;
    }

    return Draggable.create(containerSelector, options);
  }

  mouseMove = e => {
    TweenLite.to(this.myRef.current, 0.5, {
      css: {
        left: e.pageX,
        top: e.pageY
      },
      delay: 0.03
    });
  };

  handleMouseEnter = () => {
    TweenLite.to(this.myRef.current, 0, {
      css: {
        opacity: "1"
      }
    });
  };

  handleMouseLeave = () => {
    TweenLite.to(this.myRef.current, 0, {
