import React, { Component } from "react";
import { TweenLite } from "gsap";

import image1 from "./image-1.jpg";
import image2 from "./image-2.jpg";
import image3 from "./image-3.jpg";
import image4 from "./image-4.jpg";

import "./ProjectList.scss";

class projectList extends Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef();
  }

  mouseMove = e => {
    TweenLite.to(this.myRef.current, 0.5, {
      css: {
        left: e.pageX,
        top: e.pageY
      },
      delay: 0.03
    });
  };

  handleMouseEnter = () => {
    TweenLite.to(this.myRef.current, 0, {
      css: {
        opacity: "1"
      }
    });
  };

  handleMouseLeave = () => {
    TweenLite.to(this.myRef.current, 0, {
      css: {
        opacity: "0"
      }
    });
  };

  onHover = e => {
    if (e.target.className === "project p-1") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image1 + ")" }
      });
    }
    if (e.target.className === "project p-2") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image2 + ")" }
      });
    }
    if (e.target.className === "project p-3") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image3 + ")" }
      });
    }
    if (e.target.className === "project p-4") {
      TweenLite.to(this.myRef.current, 0, {
        css: { "background-image": "url(" + image4 + ")" }
      });
    }
  };

  render() {
    return (
      <React.Fragment>
        <div class="Container__wrapper Container__absolute Container__center Container__masked Cursor__wrapper">
          <div class="Cursor__cursor">
            <svg xmlns="http://www.w3.org/2000/svg" width="56" height="56" viewBox="0 0 56 56">
              <circle cx="28" cy="28" r="27" fill="transparent" stroke="#000" stroke-miterlimit="10" stroke-width="2">
              </circle>
              <g>
                <polygon points="28.064 9.543 20.871 26.834 35.129 26.834 28.064 9.543" stroke="#000" stroke-linejoin="round" stroke-width="2">
                </polygon>
                <polygon points="27.936 46.293 35.129 26.834 20.871 26.834 27.936 46.293" fill="none" stroke="#000" stroke-linejoin="round" stroke-width="2">
                </polygon>
                <ellipse cx="28" cy="26.834" rx="7.129" ry="6.679" fill="#fff" stroke="#000" stroke-miterlimit="10" stroke-width="2">
                </ellipse>
              </g>
            </svg>
          </div>
        </div>
        <div
          className="ProjectList"
          onMouseOver={this.mouseMove}
          onMouseEnter={this.handleMouseEnter}
          onMouseLeave={this.handleMouseLeave}
        >

        </div>

        <div class="Container__wrapper Container__absolute Container__center">
            <div class="Heading__wrapper styles__font-size-xs" style="position: absolute; bottom: 20%;">DRAG TO EXPLORE</div>
              <div class="lib__wrapper ">
                <div class="lib__item">
                  <a data-hide-cursor="true" class="styles__font-size-xs styles__font-family-sans-serif styles__double-border Thumb__wrapper Thumb__size-medium Thumb__layout-horizontal" draggable="false" href="/postcards/my-week">
                    <img class="Thumb__cover" alt="My week by Raffaele Filiberti — sabato.studio" draggable="false" src="/uploads/week_7ab085e40c.jpeg"/>
                    <div class="Thumb__title">My week by Raffaele Filiberti — sabato.studio
                    </div>
                  </a>
                </div>
            </div>
        </div>
      </React.Fragment>
    );
  }
}

export default projectList;
